import { ModalPermissaoPage } from './../modal-permissao/modal-permissao.page';
import { Component, OnInit } from '@angular/core';
import { ModalController, NavController, AlertController } from '@ionic/angular';
import { HttpClient, HttpHeaders } from '@angular/common/http';



@Component({
  selector: 'app-permicao',
  templateUrl: './permicao.page.html',
  styleUrls: ['./permicao.page.scss'],
})
export class PermicaoPage implements OnInit {

  linhas: any;

  constructor(
    private navCtrl: NavController,
    private modalCtrl: ModalController,
    private http: HttpClient,
    private alertCtrl: AlertController
  ) {}

  permissaonova(){
    this.navCtrl.navigateForward('newpermissao')
  }

  async showmodalPermissao(){
    const modal = await this.modalCtrl.create({
      component: ModalPermissaoPage
    })

    modal.onDidDismiss().then((data) => {
      this.montaGrid();
    });

    return await modal.present();
  }

  againpage(){
    this.navCtrl.navigateForward('register')
  }

  montaGrid(){

    const url = `http://localhost/classes/montaGridPermissao.php`;

    this.http.get(url).subscribe(data => {
      // debugger;
      this.linhas = data;

    }, error => {
      // Lide com erros aqui
      console.error(error);
    });
  }

  ngOnInit() {
  }

  ionViewDidEnter(){
    this.montaGrid()
  }

  modificaStatus(valor1: any,valor2: any){
    console.log('teste')
  }



}

<ion-content [fullscreen]="true">

  <div id="header">
    <div id="app-bar">
      <ion-icon name="" routerLink="/welcome"></ion-icon>
      <div id="logo">
        <img src="assets/icon/bannerMoio.png" alt="" />
      </div>
      <div></div>
    </div>
  </div>

  <form class="form" method="POST" action="loguin/dadoslogin.php">
    <div class="pesquisar">
      <ion-card class="form_pesquisa">
        <ion-card-content>
          <ion-item>
            <ion-icon name="search-outline"></ion-icon>
            <ion-input name="pesquisa" type="text" placeholder="Pesquisar"></ion-input>
          </ion-item>
        </ion-card-content>
      </ion-card>
    </div><br><br>

    <div class="pesqpermissoes">
      <ion-icon name="arrow-back-outline" id="voltaLocal" style="color: black;" (click)="againpage()"></ion-icon>
      <h1 style="color:black;" id="permissoes">Permissões</h1>
      <ion-button name="bntcriar" expand="block" (click)="showmodalPermissao()">Criar Nova Permissão</ion-button>
    </div>

    <div class="status">
      <ion-card class="form_status">
          <hr/>
            <ion-grid  >
              <!-- Cabeçalho da tabela -->
              <ion-row>
                <ion-col size='4' style="background-color: #addc6f;" ><b>Nome</b></ion-col>
                <ion-col size='4' style="background-color: #addc6f;"><b>Status</b></ion-col>
                <ion-col size='4' style="background-color: #addc6f;"><b>Status</b></ion-col>
              </ion-row>

              <ion-row *ngFor="let linha of linhas">
                <ion-col>{{ linha.nome }}</ion-col>
                <ion-col (click)="modificaStatus(linha.nome, linha.status)">{{ linha.status }}</ion-col>
                <ion-col><ion-icon name='create-outline'></ion-icon><ion-icon name='trash-outline'></ion-icon>
                </ion-col>
              </ion-row>

            </ion-grid>
      </ion-card>

    </div>
  </form>


</ion-content>
