import { ApiService } from './../../api.service';
import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { NavController } from '@ionic/angular';
// import { error } from 'console';
import { H}

@Component({
  selector: 'app-loguin',
  templateUrl: './loguin.page.html',
  styleUrls: ['./loguin.page.scss'],
})
export class LoguinPage implements OnInit {
// banco de dados
email: any;
senha: any;

//.
  public fGroup: FormGroup;

  constructor(
    private navCtrl: NavController,
    private fBuilder: FormBuilder) {
    this.fGroup = this.fBuilder.group({
      'email': [
        null,
        Validators.compose([
          Validators.required,
          Validators.pattern(
            /^[a-zA-Z0-9.!#$%&'*+\/=?^_`{|}~-]+@[a-zA-Z0-9](?:[a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?(?:\.[a-zA-Z0-9](?:[a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?)*$/
          ),
          Validators.minLength(4),
          Validators.maxLength(100),
        ]),
      ],
      'senha': [
        null,
        Validators.compose([
          Validators.required,
          Validators.minLength(4),
          Validators.maxLength(40),
        ]),
      ]
    });
  }

  showProfile() {
    console.log(this.fGroup.value);
    // let data = {
    //   email: this.email,
    //   senha: this.senha
    // };

    // this._apiService.showProfile(data).subscribe((res:any) =>{
    //   console.log("SUCESSO ===", res);
    // },(error: any) => {
    //   console.log("ERROR ===", error);
    // });

  }

  ngOnInit() {
    setTimeout(()=>{
      this.fGroup.get('email')?.setValue('teste@gmail.com');
      this.fGroup.get('senha')?.setValue('teste@gmail.com');
    }, 2000)
  }
}
