import { ModalPermissaoPage } from './../modal-permissao/modal-permissao.page';
import { Component, OnInit, ElementRef, Renderer2 } from '@angular/core';
import { ModalController, NavController } from '@ionic/angular';
import { HttpClient, HttpHeaders } from '@angular/common/http';



@Component({
  selector: 'app-permicao',
  templateUrl: './permicao.page.html',
  styleUrls: ['./permicao.page.scss'],
})
export class PermicaoPage implements OnInit {

  dados: any;
  gridPermissao: string = '';

  constructor(
    private navCtrl: NavController,
    private modalCtrl: ModalController,
    private http: HttpClient,
    private renderer: Renderer2,
    private el: ElementRef
  ) {}

  permissaonova(){
    this.navCtrl.navigateForward('newpermissao')
  }

  async showmodalPermissao(){
    const modal = await this.modalCtrl.create({
      component: ModalPermissaoPage
    })

    modal.present();
  }

  againpage(){
    this.navCtrl.navigateForward('register')
  }

  montaGrid(){

    const url = `http://localhost/classes/montaGridPermissao.php`;

    this.http.get(url).subscribe(data => {
      this.dados = data;
      this.gridPermissao = this.dados.html;

      const gridElement = document.createElement('ion-grid');
      // Adicione o grid à página
      this.renderer.appendChild(this.el.nativeElement, gridElement);

      // Force a atualização do estilo
      this.renderer.setStyle(gridElement, 'display', 'block');

    }, error => {
      // Lide com erros aqui
      console.error(error);
    });
  }

  ngOnInit() {
  }

  ionViewDidEnter(){
    this.montaGrid()
  }


}

<ion-content [fullscreen]="true">

  <div id="header">
    <div id="app-bar">
      <ion-icon name="" routerLink="/welcome"></ion-icon>
      <div id="logo">
        <img src="assets/icon/bannerMoio.png" alt="" />
      </div>
      <div></div>
    </div>
  </div>

  <form class="form" method="POST" action="loguin/dadoslogin.php">
    <div class="pesquisar">
      <ion-card class="form_pesquisa">
        <ion-card-content>
          <ion-item>
            <ion-icon name="search-outline"></ion-icon>
            <ion-input name="pesquisa" type="text" placeholder="Pesquisar"></ion-input>
          </ion-item>
        </ion-card-content>
      </ion-card>
    </div><br><br>

    <div class="pesqpermissoes">
      <ion-icon name="arrow-back-outline" id="voltaLocal" style="color: black;" (click)="againpage()"></ion-icon>
      <h1 style="color:black;" id="permissoes">Permissões</h1>
      <ion-button name="bntcriar" expand="block" (click)="showmodalPermissao()">Criar Nova Permissão</ion-button>
    </div>

    <div class="status">
      <ion-card class="form_status">
          <hr/>
            <ion-grid  [innerHTML]="gridPermissao">
              <!-- Cabeçalho da tabela -->

            </ion-grid>
      </ion-card>

    </div>
  </form>


</ion-content>
