import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { ModalController } from '@ionic/angular';

@Component({
  selector: 'app-modal-local',
  templateUrl: './modal-local.page.html',
  styleUrls: ['./modal-local.page.scss'],
})
export class ModalLocalPage implements OnInit {

  nomeL: any;
  cep: any;
  cidade: any;
  rua: any;
  numero: any;

  public fGroupLocal: FormGroup;

  constructor(
    private modalCtrl: ModalController,
    private fBuilder: FormBuilder
  ) {
    this.fGroupLocal = fBuilder.group({
      nomeL: [null],
      cep: [null],
      cidade: [null],
      rua: [null],
      numero: [null]
    });
  }

  close() {
    this.modalCtrl.dismiss();
  }

  showLocal(){
    // console.log(this.fGroupLocal.value)
    const nomeL = this.fGroupLocal.value.nomeL;
    const cep = this.fGroupLocal.value.cep;
    const cidade = this.fGroupLocal.value.cidade;
    const rua = this.fGroupLocal.value.rua;
    const numero = this.fGroupLocal.value.numero;
    const url = `http://localhost/classes/criarNovoLocal.php?nomeL=${nomeL}&cep=${cep}&cidade=${cidade}&rua=${rua}&numero=${numero}`;

    this.http.get(url).subscribe(data => {
      this.dados = data;
        if (this.dados.status == "sucess"){
        //aqui deu boa
        // console.log('ok')
        myalert.present();
      } else {
        //aqui deu ruim
        // console.log('recusado')
        alertCancel.present();
      }
    }, error => {
      // Lide com erros aqui
      console.error(error);
    });


  }

  ngOnInit() {
  }

}

<ion-content [fullscreen]="true">
  <div id="header">
    <div id="app-bar">
      <ion-icon name="arrow-back-outline" style="color: black;" (click)="close()"></ion-icon>
      <div id="logo">
        <img src="assets/icon/moio.png" alt="" />
      </div>
      <div></div>
    </div>
  </div>

<form [formGroup]="fGroupLocal">
  <div id="textLocal">
    <h1>Criar novo local</h1>
    <p>Cadastre um novo local para seus eventos, este cadastro é direcionado a locaslização</p>
    <div id="informacoes">
      <ion-input label="Nome do Local" formControlName="nomeL" labelPlacement="floating" [counter]="true" maxlength="100"></ion-input>
      <ion-input label="CEP" formControlName="cep" labelPlacement="floating" [counter]="true" maxlength="8"></ion-input>
      <ion-input label="Cidade" formControlName="cidade" labelPlacement="floating" [counter]="true" maxlength="70"></ion-input>
      <ion-input label="Rua" formControlName="rua" labelPlacement="floating" [counter]="true" maxlength="70"></ion-input>
      <ion-input label="Número" formControlName="numero" labelPlacement="floating" [counter]="true" maxlength="10"></ion-input>
    </div>
  </div>

  <ion-button shape="round" name="enviar" (click)="showLocal()">Criar novo local</ion-button>
</form>



</ion-content>
